import React, { useState } from 'react';
import "./BookTable.css";
import { images } from '../../constants'; 

const BookTable = () => {
    // State hooks for each form element
    const [date, setDate] = useState('');
    const [time, setTime] = useState('');
    const [location, setLocation] = useState('');
    const [partySize, setPartySize] = useState('');

    // Handle form submission
    const handleSubmit = (event) => {
        event.preventDefault();
        // Here you would typically send this data to a server or some other processing function
        alert(`Booking Details:\nDate: ${date}\nTime: ${time}\nLocation: ${location}\nParty Size: ${partySize}`);
    }

    const [modal, setmodal] =useState(false);

  const toggleModal =() =>{
    setmodal(!modal)

  };

    return (
        <div className="app__bg">

        <div id="bookTableContainer">
            <h1>Book a Table</h1>
            <form onSubmit={handleSubmit}>
                <input 
                    type="date" 
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                    required 
                />
                <input 
                    type="time" 
                    value={time}
                    onChange={(e) => setTime(e.target.value)}
                    required 
                    />
                <select 
                    value={location} 
                    onChange={(e) => setLocation(e.target.value)}
                    required
                    >
                    <option value="">Any Location</option>
                    <option value="1">Near fountain</option>
                    <option value="2">Nearfire</option>
                    <option value="3">Cabin</option>
                </select>
                <select 
                    value={partySize} 
                    onChange={(e) => setPartySize(e.target.value)}
                    required
                    >
                    <option value="">Select a Party Size</option>
                    <option value="1">1 person</option>
                    <option value="2">2 people</option>
                    <option value="3">3 people</option>

                </select>
                <button type="submit">Book Now</button>
                {modal &&(

<div className="modal">
<div className="overlay">
</div>
  <div className="modal-content">
    <h2>Know more</h2>
    <p>
    Your Table has been Booked
    </p>
    <button className='close-modal' onClick={toggleModal}>
      CLOSE
    </button>
  </div>
</div>
)}  
            </form>
        </div>
                    </div>
    );
}

export default BookTable;
