import React from 'react';
import { GiHamburgerMenu } from 'react-icons/gi';
import { MdOutlineRestaurantMenu } from 'react-icons/md';
import images from '../../constants/images';
import './Navbar.css';
import { Link } from 'react-router-dom';

const Navbar = () => {
    const [toggleMenu, setToggleMenu] = React.useState(false);

    const handleMenuItemClick = () => {
        setToggleMenu(false); // Ensure the menu closes after a selection
    };

    return (
        <nav className='app__navbar'>
            <div className='app__navbar-logo'>
                <img src={images.lecker} alt="app logo" />
            </div>
            <ul className='app__navbar-links'>
                <li className='p__opensans'><Link to='#home'>Home</Link></li>
                <li className='p__opensans'><Link to='#about'>About</Link></li>
                <li className='p__opensans'><Link to='#menu'>Menu</Link></li>
                <li className='p__opensans'><Link to='#order'>Order</Link></li>
                <li className='p__opensans'><Link to='#login'>Contact</Link></li>
            </ul>
            <div className='app__navbar-bt'>
                <Link to='/booktable' className='p__opensans'>Book Table</Link>
            </div>
            <div className='app__navbar-smallscreen'>
                <GiHamburgerMenu color='#fff' fontSize={27} onClick={() => setToggleMenu(!toggleMenu)} />
                {toggleMenu && (
                    <div className='app__navbar-smallscreen_overlay'>
                        <MdOutlineRestaurantMenu className='overlay__close' onClick={handleMenuItemClick} />
                        <ul className='app__navbar-smallscreen-links'>
                            <li className='p__opensans'><Link to='#home' onClick={handleMenuItemClick}>Home</Link></li>
                            <li className='p__opensans'><Link to='#about' onClick={handleMenuItemClick}>About</Link></li>
                            <li className='p__opensans'><Link to='#menu' onClick={handleMenuItemClick}>Menu</Link></li>
                            <li className='p__opensans'><Link to='#order' onClick={handleMenuItemClick}>Order</Link></li>
                            <li className='p__opensans'><Link to='#login' onClick={handleMenuItemClick}>Contact</Link></li>
                        </ul>
                    </div>
                )}
            </div>
        </nav>
    );
}

export default Navbar;
