import React, { useState } from 'react';
import "./BookTable.css";
import { images } from '../../constants'; 

const BookTable = () => {
    // State hooks for each form element
    const [date, setDate] = useState('');
    const [time, setTime] = useState('');
    const [location, setLocation] = useState('');
    const [partySize, setPartySize] = useState('');

    // Handle form submission
    const handleSubmit = (event) => {
        event.preventDefault();
        // Here you would typically send this data to a server or some other processing function
        alert(`Booking Details:\nDate: ${date}\nTime: ${time}\nLocation: ${location}\nParty Size: ${partySize}`);
    };

    return (
        <div className="login__bg">

        <div id="bookTableContainer">
            <h1>Book a Table</h1>
            <form onSubmit={handleSubmit}>
                <input 
                    type="date" 
                    value={date}
                    onChange={(e) => setDate(e.target.value)}
                    required 
                />
                <input 
                    type="time" 
                    value={time}
                    onChange={(e) => setTime(e.target.value)}
                    required 
                    />
                <select 
                    value={location} 
                    onChange={(e) => setLocation(e.target.value)}
                    required
                    >
                    <option value="">Any Location</option>
                    {/* Other location options would be added here */}
                </select>
                <select 
                    value={partySize} 
                    onChange={(e) => setPartySize(e.target.value)}
                    required
                    >
                    <option value="">Select a Party Size</option>
                    {/* Party size options would be added here */}
                </select>
                <button type="submit">Book Now</button>
            </form>
        </div>
                    </div>
    );
}

export default BookTable;
