import React from 'react';
import { useState } from 'react';
import { SubHeading } from '../../components';
import { images } from '../../constants';
import './Header.css';

const Header = () => {

  const [modal, setModal] = useState(false);
  const toggleModal = () =>{
    setModal(!modal)
  }
  return (
    <div className="app__header app__wrapper section__padding" id="home">
      <div className="app__wrapper_info">
        <SubHeading title="Chase the new flavour" />
        <h1 className="app__header-h1">A Treat for the Senses</h1>
        <p className="p__opensans" style={{ margin: '2rem 0' }}>Spices sing, regions boast unique flavors, a symphony for your senses. </p>
        <a href='#Menu'>
          <button type="button" className="custom__button" onClick={toggleModal}>
            Explore Menu
          </button>

          {modal && (

            <div className="modal">
            <div className="overlay"></div>
            <div className="modal-content">
              <h2>About Us</h2>
              <p>
              Lorem Ipsum Dolor Sit Amet, Consectetur Adipiscing Elit. Quis Pharetra Adipiscing Ultrices Vulputate Posuere Tristique. In Sed Odio Nec Aliquet Eu Proin Mauris Et.
              </p>
              <button className='close-modal' onClick={toggleModal}>
              Close
              </button>
            </div>
          </div>
            )}
        </a>
      </div>

      <div className="app_wrapper_img">
        <img src={images.welcome} alt="header_img" />
      </div>
    </div>
  );
};

export default Header;
