import React, { useRef } from 'react'; // Import useRef from React
import { SubHeading } from '../../components';
import { images } from '../../constants';
import './Header.css';

const Header = () => {
  const specialMenuRef = useRef(null);

  const handleMenuItemClick = () => {
    // Check if specialMenuRef is not null before accessing scrollIntoView
    if (specialMenuRef.current) {
      specialMenuRef.current.scrollIntoView({ behavior: 'smooth' });
    }
  };

  return (
    <div className="app__header app__wrapper section__padding" id="home">
      <div className="app__wrapper_info">
        <SubHeading title="Chase the new flavour" />
        <h1 className="app__header-h1">A Treat for the Senses</h1>
        <p className="p__opensans" style={{ margin: '2rem 0' }}>Spices sing, regions boast unique flavors, a symphony for your senses. </p>
        <button type="button" className="custom__button" onClick={handleMenuItemClick}>
          Explore Menu
        </button>
      </div>

      <div className="app_wrapper_img">
        <img src={images.welcome} alt="header_img" />
      </div>

      {/* Add a reference to the special menu */}
      <div ref={specialMenuRef}></div>
    </div>
  );
};

export default Header;
