import React, { useState } from 'react';
import Modal from './Modal'; // Assuming you have a reusable Modal component
import { images } from '../../constants';
import './AboutUs.css';

const AboutUs = () => {
  const [modalContent, setModalContent] = useState('');
  const [modalOpen, setModalOpen] = useState(false);

  const toggleModal = (content) => {
    setModalContent(content);
    setModalOpen(!modalOpen);
  };

  const sections = [
    {
      title: 'About Us',
      content: 'The restaurant serves exclusive Indian dishes that hail from the rich cultural roots of India. It endeavors to promote India\'s diversity by showcasing the culinary richness of its various regions to the world. The restaurant offers a tantalizing array of dishes representing the diverse cuisines found across the Indian mainland.',
    },
    {
      title: 'Our History',
      content: 'The history of this restaurant mirrors that of India itself. Established in 1954 by a German man with a profound love for Indian cuisine, who eventually settled in India, the restaurant has steadfastly served the same delightful fare ever since.',
    },
    // Add more sections as needed
  ];

  return (
    <div className="app__aboutus app__bg flex__center section__padding" id="about">
      <div className="app__aboutus-overlay flex__center">
        <img src={images.G} alt="G_overlay" />
      </div>

      <div className="app__aboutus-content flex__center">
        {sections.map((section, index) => (
          <div className="app__aboutus-section" key={index}>
            <h1 className="headtext__cormorant">{section.title}</h1>
            <img src={images.spoon} alt="about_spoon" className="spoon__img" />
            <p className="p__opensans">{section.content}</p>
            <button type="button" className="custom__button" onClick={() => toggleModal(section)}>Know More</button>
          </div>
        ))}
      </div>

      <Modal isOpen={modalOpen} onClose={toggleModal}>
        <>
          <h2>{modalContent.title}</h2>
          <p>{modalContent.content}</p>
        </>
      </Modal>
    </div>
  );
};

export default AboutUs;
